--
-- Дамп данных таблицы `deputies`
--

INSERT INTO `deputies` (`id`, `name`, `date_of_birthday`, `position`, `bio`, `created_at`, `updated_at`, `deleted_at`, `photo`, `social`) VALUES
(1, 'Василь Петрович Олешко', '25.10.1946', 'Голова партії', 'Народився 8 жовтня 1983 року у Бердичеві Житомирської області. Закінчив Київський інститут міжнародних відносин за спеціальністю «Міжнародні економічні відносини» та Національний університет Державної податкової служби за спеціальністю «Правознавство». Політтехнолог, політичний аналітик. Керуючий партнер та Директор «Ukrainian Politconsulting Group». Обіймав керівні посади у комерційному та державному секторах, а також в політичних партіях. Працював на виборчих кампаніях з 2006 року. У президентській кампанії 2019 року був спікером виборчого штабу кандидата у президенти України Володимира Зеленського.', '2019-10-21 17:02:00', '2019-10-22 15:51:53', NULL, 'deputies\\October2019\\Coxyzb92obXOL0kiOlLY.jpg', 'https://www.facebook.com/groups/Pidgorodne.Info/?fref=nf'),
