INSERT INTO `first_blocks` (`id`, `created_at`, `updated_at`, `deleted_at`, `title`, `body`, `faicon`) VALUES
(1, '2019-10-21 16:09:00', '2019-11-08 16:37:52', NULL, 'Для кого ця партія', 'Для чесних, порядних людей, які готові служити народу, які готові змінювати країну та відстоювати головну позицію партії — побудувати країну, у якій хочеться жити!', 'voyager-smile'),
(2, '2019-10-21 16:14:00', '2019-11-08 16:37:24', NULL, 'Основні цілі', 'Зробити українців рівними перед законом, народних обранців — відповідальними, покарати корупціонерів. Дати економіці розвиватись, навести лад в системі, побудувати гуманітарну політику, яка єднає Україну.', 'voyager-dashboard'),
(3, '2019-10-21 16:15:00', '2019-11-08 16:36:58', NULL, 'Цінності', 'Прозора діяльність, відкритість до народу, відповідальність перед країною, командна робота. Учасники партії завжди і у всьому повинні бути чесними та залишатися людьми!', 'voyager-heart');

